import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  Box,
  Tabs,
  Tab,
  CircularProgress,
  Alert,
} from '@mui/material';
import {
  Bookmark as BookmarkIcon,
  BookmarkBorder as BookmarkBorderIcon,
} from '@mui/icons-material';
import axios from '../utils/axios';
import { useAuth } from '../contexts/AuthContext';

const ResourceCard = ({ resource, onBookmark, isBookmarked }) => {
  const navigate = useNavigate();

  return (
    <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      <CardContent sx={{ flexGrow: 1 }}>
        <Typography gutterBottom variant="h6" component="h2">
          {resource.title}
        </Typography>
        <Typography color="text.secondary" gutterBottom>
          {resource.category}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {resource.description}
        </Typography>
      </CardContent>
      <CardActions>
        <Button
          size="small"
          color="primary"
          onClick={() => window.open(resource.url, '_blank')}
        >
          Visit Resource
        </Button>
        <Button
          size="small"
          color="primary"
          onClick={() => onBookmark(resource)}
          startIcon={isBookmarked ? <BookmarkIcon /> : <BookmarkBorderIcon />}
        >
          {isBookmarked ? 'Bookmarked' : 'Bookmark'}
        </Button>
      </CardActions>
    </Card>
  );
};

const Home = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [trending, setTrending] = useState({
    local: [],
    github: [],
    arxiv: [],
  });
  const [loading, setLoading] = useState(true);
  const [tabValue, setTabValue] = useState(0);
  const [bookmarks, setBookmarks] = useState(new Set());
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchTrending();
    if (user) {
      fetchBookmarks();
    }
  }, [user]);

  const fetchTrending = async () => {
    try {
      setLoading(true);
      setError(null);

      // Fetch all resources
      const response = await axios.get('/api/resources');
      const resources = response.data.resources;

      // Filter resources by category
      const filteredResources = {
        local: resources.filter(r => r.category === 'Local'),
        github: resources.filter(r => r.category === 'GitHub'),
        arxiv: resources.filter(r => r.category === 'arXiv')
      };

      console.log('Fetched resources:', filteredResources);

      setTrending(filteredResources);
    } catch (err) {
      console.error('Error fetching trending resources:', err);
      setError('Failed to fetch trending resources');
    } finally {
      setLoading(false);
    }
  };

  const fetchBookmarks = async () => {
    try {
      const response = await axios.get('/api/bookmarks');
      const bookmarkIds = new Set(response.data.bookmarks.map(b => b.resource.id));
      setBookmarks(bookmarkIds);
    } catch (error) {
      console.error('Error fetching bookmarks:', error);
    }
  };

  const handleBookmark = async (resource) => {
    if (!user) {
      navigate('/login');
      return;
    }

    try {
      if (bookmarks.has(resource.id)) {
        await axios.delete(`/api/bookmarks/${resource.id}`);
        setBookmarks(prev => {
          const newBookmarks = new Set(prev);
          newBookmarks.delete(resource.id);
          return newBookmarks;
        });
      } else {
        await axios.post(`/api/bookmarks/${resource.id}`);
        setBookmarks(prev => new Set([...prev, resource.id]));
      }
    } catch (error) {
      console.error('Error toggling bookmark:', error);
    }
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        Trending AI Resources
      </Typography>

      <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
        <Tabs value={tabValue} onChange={handleTabChange}>
          <Tab label="Local Resources" />
          <Tab label="GitHub Repositories" />
          <Tab label="arXiv Papers" />
        </Tabs>
      </Box>

      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
          <CircularProgress />
        </Box>
      ) : error ? (
        <Alert severity="error" sx={{ my: 2 }}>
          {error}
        </Alert>
      ) : trending[tabValue]?.length === 0 ? (
        <Alert severity="info" sx={{ my: 2 }}>
          No resources found. Be the first to add one!
        </Alert>
      ) : (
        <Grid container spacing={3}>
          {trending[tabValue].map((resource) => (
            <Grid item xs={12} sm={6} md={4} key={resource.id}>
              <ResourceCard
                resource={resource}
                onBookmark={handleBookmark}
                isBookmarked={bookmarks.has(resource.id)}
              />
            </Grid>
          ))}
        </Grid>
      )}
    </Container>
  );
};

export default Home;
